INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (297,'Rubrique Liste de Documents - Type Acteur','portlet_document_actor_list.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:param name=\"site-path\" select=\"site-path\" />\r\n<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n<xsl:template match=\"portlet\">\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n	<div class=\"portlet-background-colored  {$device_class} append-bottom\" >\r\n        <xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n			<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n			</h3>\r\n        </xsl:if>\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\" >\r\n		     <ul>        \r\n                 <xsl:apply-templates select=\"document-list-portlet/document\" />	\r\n            </ul>  \r\n		</div>            	\r\n	</div>\r\n</xsl:template>\r\n\r\n\r\n<xsl:template match=\"document\">      \r\n    <li style=\"list-style:none;\"> \r\n        <xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n             <table width=\"90%\" summary=\"document\"> \r\n                 <tr>\r\n                     <td  width=\"65%\">   \r\n                         <a href=\"{$site-path}?document_id={document-id}&#38;portlet_id={$portlet-id}\" target=\"_top\">      \r\n                             <xsl:value-of disable-output-escaping=\"yes\" select=\"document-xml-content/actor/actor-firstname\" />&#160;<xsl:value-of disable-output-escaping=\"yes\" select=\"document-xml-content/actor/actor-lastname\" />\r\n\r\n                         </a>\r\n						<xsl:if test=\"(string(resource-is-votable)=\'1\')\">\r\n							<br />\r\n				        	<xsl:variable name=\"resource-score\" select=\"resource-score\" />\r\n	        				<img src=\"images/local/skin/plugins/rating/stars_{$resource-score}.png\" alt=\"Score\" title=\"Score\" />\r\n				        </xsl:if>   \r\n                         <xsl:if test=\"(string(is-download-stat)=\'1\')\">\r\n							<br />\r\n							#i18n{rating.resource_vote.labelDownloadCount} : <xsl:value-of select=\"resource-download-stat\" />\r\n						 </xsl:if>\r\n                    </td>\r\n                    <td  width=\"35%\">\r\n                        <xsl:apply-templates select=\"document-xml-content/actor/actor-photo/file-resource\" />\r\n                   </td>\r\n                </tr>    \r\n            </table> \r\n        </xsl:if>\r\n    </li>        \r\n</xsl:template>              \r\n	\r\n\r\n<xsl:template match=\"file-resource\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"(resource-content-type=\'image/jpeg\' or resource-content-type=\'image/jpg\' or  resource-content-type=\'image/pjpeg\' or resource-content-type=\'image/gif\' or resource-content-type=\'image/png\')\" >\r\n			<img src=\"document?id={resource-document-id}&amp;id_attribute={resource-attribute-id}\" alt=\"\" align=\"right\" width=\"40\" height=\"40\"/>\r\n		</xsl:when>\r\n		<xsl:otherwise>\r\n             <a href=\"document?id={resource-document-id}&amp;id_attribute={resource-attribute-id}\"> \r\n			   <img src=\"images/admin/skin/plugins/document/filetypes/file.png\" border=\"0\" alt=\"\" align=\"right\" width=\"40\" height=\"40\"/>\r\n             </a>\r\n		</xsl:otherwise>        \r\n	</xsl:choose>\r\n</xsl:template>\r\n\r\n</xsl:stylesheet>\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (298,'Rubrique Liste de Documents - Défaut','portlet_document_list.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:param name=\"site-path\" select=\"site-path\" />\r\n<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n    \r\n<xsl:template match=\"portlet\">\r\n\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n    <div class=\"portlet-background-colored  {$device_class} append-bottom\" >\r\n        <xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n            <h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n                <xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n            </h3>\r\n        </xsl:if>\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\" >\r\n            <xsl:apply-templates select=\"document-list-portlet\" />\r\n		</div>\r\n    </div>\r\n</xsl:template>\r\n\r\n\r\n<xsl:template match=\"document-list-portlet\">\r\n    <ul>\r\n        <xsl:apply-templates select=\"document\" />\r\n    </ul>\r\n</xsl:template>\r\n\r\n\r\n<xsl:template match=\"document\">\r\n    <li>\r\n        <a href=\"{$site-path}?document_id={document-id}&#38;portlet_id={$portlet-id}\" target=\"_top\">\r\n            <xsl:for-each select=\"descendant::*\">\r\n                <xsl:value-of select=\"document-title\" />\r\n           </xsl:for-each>\r\n		</a>          \r\n		<xsl:if test=\"(string(resource-is-votable)=\'1\')\">\r\n			<br />\r\n        	<xsl:variable name=\"resource-score\" select=\"resource-score\" />\r\n	        <img src=\"images/local/skin/plugins/rating/stars_{$resource-score}.png\" alt=\"Score\" title=\"Score\" />\r\n        </xsl:if>   \r\n           <br />\r\n            <xsl:for-each select=\"descendant::*\">\r\n                <xsl:value-of select=\"document-summary\" />\r\n            </xsl:for-each>\r\n            <br />\r\n            <xsl:if test=\"(string(document-is-commentable)=\'1\')\">\r\n                <img src=\"images/local/skin/plugins/document/nb_comments.png\" alt=\"Nombre de commentaires\" title=\"Nombre de commentaires\" align=\"middle\"/>&#160;<xsl:value-of select=\"document-comment-nb\" />      \r\n            </xsl:if>   \r\n    </li>\r\n</xsl:template>\r\n\r\n\r\n</xsl:stylesheet>\r\n\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (299,'Rubrique Liste de Documents - Type Brève','portlet_document_shortarticle_list.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\"\r\n	xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n	<xsl:param name=\"site-path\" select=\"site-path\" />\r\n	<xsl:param name=\"page-id\" select=\"page-id\" />\r\n	<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n	<xsl:template match=\"portlet\">\r\n	\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n	\r\n		<div class=\"portlet  {$device_class} -lutece-border-radius append-bottom\">\r\n			<xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n				<h3 id=\"article_{$portlet-id}\">\r\n					<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n				</h3><br />\r\n			</xsl:if>\r\n			<xsl:apply-templates select=\"document-list-portlet/document\" />\r\n			<xsl:text disable-output-escaping=\"yes\">\r\n				<![CDATA[<div class=\"clear\">&#160;</div>]]>\r\n			</xsl:text>\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"document\">\r\n		<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n			<div class=\"span-6 portlet -lutece-border-radius append-bottom\">\r\n				<a\r\n					href=\"{$site-path}?document_id={document-id}&#38;portlet_id={$portlet-id}\"\r\n					target=\"_top\">\r\n					<xsl:for-each select=\"descendant::*\">\r\n						<xsl:value-of select=\"document-title\" />\r\n					</xsl:for-each>\r\n				</a>\r\n				<xsl:if test=\"(string(resource-is-votable)=\'1\')\">\r\n					<br />\r\n		        	<xsl:variable name=\"resource-score\" select=\"resource-score\" />\r\n	        		<img src=\"images/local/skin/plugins/rating/stars_{$resource-score}.png\" alt=\"Score\" title=\"Score\" />\r\n		        </xsl:if>   \r\n				<xsl:if test=\"(string(is-download-stat)=\'1\')\">\r\n					<br />\r\n					#i18n{rating.resource_vote.labelDownloadCount} : <xsl:value-of select=\"resource-download-stat\" />\r\n				</xsl:if>\r\n				<br />\r\n				<xsl:for-each select=\"descendant::*\">\r\n					<xsl:value-of select=\"document-summary\" />\r\n				</xsl:for-each>\r\n			</div>\r\n		</xsl:if>\r\n	</xsl:template>\r\n	\r\n</xsl:stylesheet>\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (300,'Rubrique Liste de Documents - Type Article','portlet_document_article_list.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\"\r\n	xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n	<xsl:param name=\"site-path\" select=\"site-path\" />\r\n	<xsl:param name=\"target\" select=\"target\" />\r\n	<xsl:param name=\"page-id\" select=\"page-id\" />\r\n	<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n	<xsl:template match=\"portlet\">\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n	\r\n		<div class=\"portlet  {$device_class} -lutece-border-radius append-bottom\">\r\n			<xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n				<h3 id=\"article_{$portlet-id}\">\r\n					<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n				</h3><br />\r\n			</xsl:if>\r\n			<xsl:apply-templates select=\"document-list-portlet/document\" />\r\n			<xsl:text disable-output-escaping=\"yes\">\r\n				<![CDATA[<div class=\"clear\">&#160;</div>]]>\r\n			</xsl:text>\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"document\">\r\n		<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n			<div class=\"span-6 portlet -lutece-border-radius append-bottom\">\r\n				<a\r\n					href=\"{$site-path}?document_id={document-id}&#38;portlet_id={$portlet-id} {$target}\">\r\n					<xsl:for-each select=\"descendant::*\">\r\n						<xsl:value-of select=\"document-title\" />\r\n					</xsl:for-each>\r\n				</a>\r\n				<xsl:if test=\"(string(resource-is-votable)=\'1\')\">\r\n					<br />\r\n	        		<xsl:variable name=\"resource-score\" select=\"resource-score\" />\r\n	        		<img src=\"images/local/skin/plugins/rating/stars_{$resource-score}.png\" alt=\"Score\" title=\"Score\" />\r\n		        </xsl:if>   \r\n				<xsl:if test=\"(string(is-download-stat)=\'1\')\">\r\n					<br />\r\n					#i18n{rating.resource_vote.labelDownloadCount} : <xsl:value-of select=\"resource-download-stat\" />\r\n				</xsl:if>\r\n				<br />\r\n				<xsl:for-each select=\"descendant::*\">\r\n					<xsl:value-of select=\"document-summary\" />\r\n				</xsl:for-each>\r\n			</div>\r\n		</xsl:if>\r\n	</xsl:template>\r\n	\r\n</xsl:stylesheet>\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (301,'Rubrique Liste de Documents - Type Son','portlet_document_sound_list.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:param name=\"site-path\" select=\"site-path\" />\r\n<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n<xsl:template match=\"portlet\">\r\n\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n	<div class=\"portlet-background-colored  {$device_class} append-bottom\" >\r\n        <xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n			<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n			</h3>\r\n        </xsl:if>\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\" >\r\n		     <ul>\r\n     	       <xsl:apply-templates select=\"document-list-portlet/document\" />\r\n     	     </ul>  \r\n		</div>\r\n	</div>\r\n</xsl:template>\r\n\r\n\r\n<xsl:template match=\"document\">      \r\n<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n    <li>\r\n        <a href=\"{$site-path}?document_id={document-id}&amp;portlet_id={$portlet-id}\" target=\"_top\">      \r\n             <xsl:value-of select=\"document-xml-content/sound/sound-title\" />   \r\n       </a>\r\n       	<xsl:if test=\"(string(resource-is-votable)=\'1\')\">\r\n			<br />\r\n        	<xsl:variable name=\"resource-score\" select=\"resource-score\" />\r\n	        <img src=\"images/local/skin/plugins/rating/stars_{$resource-score}.png\" alt=\"Score\" title=\"Score\" />\r\n        </xsl:if>   \r\n		<xsl:if test=\"(string(is-download-stat)=\'1\')\">\r\n			<br />\r\n			#i18n{rating.resource_vote.labelDownloadCount} : <xsl:value-of select=\"resource-download-stat\" />\r\n		</xsl:if>\r\n       <br />\r\n          <xsl:value-of select=\"document-xml-content/sound/sound-file\" />\r\n       <br />\r\n          <xsl:value-of select=\"document-xml-content/sound/sound-author\" />\r\n   </li>       \r\n</xsl:if>\r\n</xsl:template>              \r\n	\r\n\r\n</xsl:stylesheet>\r\n\r\n\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (302,'Rubrique Liste de Documents - Type Pdf','portlet_document_pdf_list.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:param name=\"site-path\" select=\"site-path\" />\r\n<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n<xsl:template match=\"portlet\">\r\n\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n	<div class=\"portlet-background-colored {$device_class} append-bottom\" >\r\n        <xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n			<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n			</h3>\r\n        </xsl:if>\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\" >\r\n		     <ul>\r\n         	       <xsl:apply-templates select=\"document-list-portlet/document\" />\r\n             </ul>  \r\n		</div>\r\n	</div>\r\n</xsl:template>\r\n\r\n\r\n<xsl:template match=\"document\">      \r\n<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n   <!-- <li>\r\n        <a href=\"{$site-path}?document_id={document-id}&amp;portlet_id={$portlet-id}\" target=\"_top\">      \r\n             <xsl:value-of select=\"document-xml-content/pdf/document-title\" />   \r\n        </a>\r\n		<br />\r\n		<xsl:value-of select=\"document-xml-content/pdf/document-summary\" />   \r\n	\r\n    </li>-->\r\n    <li>\r\n        <a href=\"document?id={document-id}&amp;id_attribute=48\" target=\"_blank\">      \r\n        	<xsl:for-each select=\"descendant::*\">\r\n                <xsl:apply-templates select=\"file-resource\" />\r\n           </xsl:for-each>  \r\n          	<xsl:for-each select=\"descendant::*\">\r\n                <xsl:value-of select=\"document-title\" />\r\n           </xsl:for-each>  \r\n        </a>\r\n        <xsl:if test=\"(string(resource-is-votable)=\'1\')\">\r\n			<br />\r\n        	<xsl:variable name=\"resource-score\" select=\"resource-score\" />\r\n	        <img src=\"images/local/skin/plugins/rating/stars_{$resource-score}.png\" alt=\"Score\" title=\"Score\" />\r\n        </xsl:if>   \r\n        <xsl:if test=\"(string(is-download-stat)=\'1\')\">\r\n			<br />\r\n			#i18n{rating.resource_vote.labelDownloadCount} : <xsl:value-of select=\"resource-download-stat\" />\r\n		</xsl:if>\r\n		<br />\r\n	    <xsl:for-each select=\"descendant::*\">\r\n        	<xsl:value-of select=\"document-summary\" />\r\n        </xsl:for-each>\r\n    </li>	\r\n</xsl:if>\r\n</xsl:template>              \r\n\r\n<xsl:template match=\"file-resource\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"(resource-content-type=\'application/pdf\')\" >\r\n			<img src=\"images/local/skin/plugins/document/filetypes/pdf.png\" border=\"0\" alt=\"\" width=\"32\" height=\"32\" />\r\n		</xsl:when>\r\n\r\n		<xsl:otherwise>\r\n		   <img src=\"images/local/skin/plugins/document/filetypes/file.png\" border=\"0\" alt=\"\" width=\"32\" height=\"32\"/>\r\n		</xsl:otherwise>        \r\n	</xsl:choose>\r\n	</xsl:template>\r\n</xsl:stylesheet>\r\n\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (303,'Rubrique Liste de Documents - Type Video','portlet_document_video_list.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:param name=\"site-path\" select=\"site-path\" />\r\n<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n<xsl:template match=\"portlet\">\r\n\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n	<div class=\"portlet-background-colored  {$device_class} append-bottom\" >\r\n        <xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n			<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n			</h3>\r\n        </xsl:if>\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\" >\r\n		     <ul>\r\n         	       <xsl:apply-templates select=\"document-list-portlet/document\" />\r\n             </ul>  \r\n		</div>\r\n	</div>\r\n</xsl:template>\r\n\r\n\r\n<xsl:template match=\"document\">      \r\n<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n    <li>\r\n        <a href=\"{$site-path}?document_id={document-id}&amp;portlet_id={$portlet-id}\" target=\"_top\">      \r\n             <xsl:value-of select=\"document-xml-content/video/video-title\" />   \r\n       </a>\r\n       	<xsl:if test=\"(string(resource-is-votable)=\'1\')\">\r\n			<br />\r\n        	<xsl:variable name=\"resource-score\" select=\"resource-score\" />\r\n	        <img src=\"images/local/skin/plugins/rating/stars_{$resource-score}.png\" alt=\"Score\" title=\"Score\" />\r\n        </xsl:if>   \r\n       <xsl:if test=\"(string(is-download-stat)=\'1\')\">\r\n			<br />\r\n			#i18n{rating.resource_vote.labelDownloadCount} : <xsl:value-of select=\"resource-download-stat\" />\r\n		</xsl:if>\r\n       <br />\r\n          <xsl:value-of select=\"document-xml-content/video/video-file\" />\r\n    </li>\r\n</xsl:if>\r\n</xsl:template>              \r\n\r\n\r\n</xsl:stylesheet>\r\n\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (304,'Rubrique Liste de Documents - Type Image','portlet_document_image_list.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:param name=\"site-path\" select=\"site-path\" />\r\n<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n<xsl:template match=\"portlet\">\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n	<div class=\"portlet-background-colored {$device_class} append-bottom\" >\r\n        <xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n			<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n			</h3>\r\n        </xsl:if>\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\" >\r\n		     <ul>        \r\n                 <xsl:apply-templates select=\"document-list-portlet/document\" />	\r\n            </ul>  \r\n		</div>            	\r\n	</div>\r\n</xsl:template>\r\n\r\n\r\n<xsl:template match=\"document\">      \r\n    <li style=\"list-style:none;\"> \r\n        <xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n            <a href=\"{$site-path}?document_id={document-id}&#38;portlet_id={$portlet-id}\" target=\"_top\">      \r\n                <xsl:apply-templates select=\"document-xml-content/image/image-file/file-resource\" />\r\n            </a>\r\n            <xsl:if test=\"(string(resource-is-votable)=\'1\')\">\r\n				<br />\r\n	        	<xsl:variable name=\"resource-score\" select=\"resource-score\" />\r\n	        	<img src=\"images/local/skin/plugins/rating/stars_{$resource-score}.png\" alt=\"Score\" title=\"Score\" />\r\n        	</xsl:if>   \r\n            <xsl:if test=\"(string(is-download-stat)=\'1\')\">\r\n				<br />\r\n				#i18n{rating.resource_vote.labelDownloadCount} : <xsl:value-of select=\"resource-download-stat\" />\r\n			</xsl:if>\r\n            <br />           \r\n                <xsl:value-of disable-output-escaping=\"yes\" select=\"document-xml-content/image/image-description\" />\r\n            <br />\r\n                <xsl:value-of disable-output-escaping=\"yes\" select=\"document-xml-content/image/image-credits\" />\r\n            <br />\r\n        </xsl:if>\r\n    </li>        \r\n</xsl:template>              \r\n	\r\n\r\n<xsl:template match=\"file-resource\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"(resource-content-type=\'image/jpeg\' or resource-content-type=\'image/jpg\' or  resource-content-type=\'image/pjpeg\' or resource-content-type=\'image/gif\' or resource-content-type=\'image/png\')\" >\r\n			<img src=\"document?id={resource-document-id}&amp;id_attribute={resource-attribute-id}\" alt=\"\" border=\"0\" width=\"120\" height=\"120\"/>\r\n		</xsl:when>\r\n		<xsl:otherwise>\r\n             <a href=\"document?id={resource-document-id}&amp;id_attribute={resource-attribute-id}\"> \r\n			   <img src=\"images/admin/skin/plugins/document/filetypes/file.png\" border=\"0\" alt=\"\" width=\"120\" height=\"120\"/>\r\n             </a>\r\n		</xsl:otherwise>        \r\n	</xsl:choose>\r\n</xsl:template>\r\n\r\n</xsl:stylesheet>\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (305,'Rubrique Liste de Documents - Type Acteur Service','portlet_document_actor_list_service.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:param name=\"site-path\" select=\"site-path\" />\r\n<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n		\r\n\r\n<xsl:template match=\"portlet\">\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n	<div class=\"portlet-background-colored  {$device_class} append-bottom\" >\r\n        <xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n			<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n			</h3>\r\n        </xsl:if>\r\n        \r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\" >\r\n			\r\n			<script type=\"text/javascript\">\r\n				<![CDATA[\r\n			      function displayId(baliseId)\r\n				  {\r\n				  \r\n				  if (document.getElementById(baliseId) != null)\r\n				    {\r\n				    	document.getElementById(baliseId).style.visibility=\'visible\';\r\n				    	document.getElementById(baliseId).style.display=\'block\';\r\n				    }\r\n				  }\r\n				\r\n				function hideId(baliseId)\r\n				  {\r\n				  \r\n				  if (document.getElementById(baliseId) != null)\r\n				    {\r\n				   		document.getElementById(baliseId).style.visibility=\'hidden\';\r\n				    	document.getElementById(baliseId).style.display=\'none\';\r\n				    }\r\n				  }\r\n				  \r\n				  ]]>\r\n		   	</script>\r\n			\r\n			<ul>	\r\n			<!-- Tri la liste d\'acteurs par service dans l\'ordre alphabétique -->\r\n			<xsl:apply-templates select=\"document-list-portlet/document/document-xml-content/actor\">\r\n		       <xsl:sort select=\"actor-service\" order=\"ascending\"/>\r\n		    </xsl:apply-templates>\r\n			</ul>\r\n		</div>            	\r\n	</div>\r\n</xsl:template>\r\n\r\n\r\n<xsl:template match=\"actor\">\r\n\r\n     <xsl:call-template name=\"sortieNoeuds\"/>\r\n\r\n</xsl:template>\r\n\r\n<xsl:template name=\"sortieNoeuds\">\r\n	<xsl:variable name=\"indiceNoeud\" select=\"position()\"/>\r\n	<xsl:variable name=\"current\"><xsl:value-of select=\"actor-service\"/></xsl:variable>\r\n	<!-- On récupère le noeud suivant dans la liste d\'actor triée -->\r\n	<xsl:variable name=\"suivant\">\r\n		<xsl:if test=\"not(position()=last())\">\r\n			<xsl:call-template name=\"noeudSuivant\">\r\n				<xsl:with-param name=\"indiceNoeudSuivant\" select=\"$indiceNoeud + 1\"/>\r\n			</xsl:call-template>\r\n		</xsl:if>\r\n	</xsl:variable>\r\n	\r\n	<!-- On récupère le noeud précédent dans la liste d\'actor triée -->\r\n	<xsl:variable name=\"precedent\">\r\n		<xsl:if test=\"position() != 1\">\r\n			<xsl:call-template name=\"noeudSuivant\">\r\n				<xsl:with-param name=\"indiceNoeudSuivant\" select=\"$indiceNoeud - 1\"/>\r\n			</xsl:call-template>\r\n		</xsl:if>\r\n	</xsl:variable>\r\n	\r\n	<!-- on affiche le service et l\'acteur  -->\r\n	<xsl:call-template name=\"display\">\r\n			<xsl:with-param name=\"current\" select=\"$current\"/>\r\n			<xsl:with-param name=\"precedent\" select=\"$precedent\"/>\r\n			<xsl:with-param name=\"suivant\" select=\"$suivant\"/>\r\n		</xsl:call-template>\r\n	\r\n</xsl:template>\r\n        \r\n<xsl:template name=\"noeudSuivant\">\r\n	<xsl:param name=\"indiceNoeudSuivant\" select=\"0\"/>\r\n	<xsl:for-each select=\"/portlet/document-list-portlet/document/document-xml-content/actor\">\r\n		<xsl:sort select=\"actor-service\" order=\"ascending\"/>\r\n		<xsl:if test=\"position()=$indiceNoeudSuivant\">\r\n			<xsl:value-of select=\"actor-service\"/>\r\n		</xsl:if>\r\n	</xsl:for-each>\r\n</xsl:template> \r\n\r\n<xsl:template name=\"display\">\r\n	<xsl:param name=\"current\"/>\r\n	<xsl:param name=\"precedent\"/>\r\n	<xsl:param name=\"suivant\"/>\r\n	\r\n\r\n	<xsl:if test=\"not(string($current) = \'\')\">\r\n	\r\n	<xsl:choose>\r\n		<!-- Si le service n\'a qu\'un seul acteur -->\r\n	  <xsl:when test=\"string($precedent) != string($current) and string($suivant) != string($current)\">\r\n	    \r\n	    <li style=\"list-style:none;\" name=\"\">\r\n			<!-- on affiche le service  -->\r\n			<h2>\r\n	           <a href=\"javascript:displayId(\'{$current}\');\"><img src=\"images/admin/skin/plus.gif\" alt=\"\"/></a>\r\n	           <a href=\"javascript:hideId(\'{$current}\');\"><img src=\"images/admin/skin/minus.gif\" alt=\"\"/></a>\r\n	           <xsl:value-of disable-output-escaping=\"yes\" select=\"actor-service\" />\r\n	       </h2>\r\n	       \r\n	       <div style=\"visibility: hidden; display: none;\" id=\"{$current}\">\r\n	       	<!-- on affiche l\'acteur  -->\r\n	       	<ul>\r\n	       		<xsl:call-template name=\"nameactor\"/>\r\n	       	</ul>\r\n	       	&#160;\r\n	       </div>\r\n		</li>\r\n	  </xsl:when>\r\n	  <!-- Si le service a plusieur acteur et que l\'on est sur le premier acteur -->\r\n	  <xsl:when test=\"string($precedent) != string($current) and string($suivant) = string($current)\">\r\n			\r\n			<xsl:text disable-output-escaping=\"yes\">&lt;</xsl:text>li style=\"list-style:none;\" name=\"\"<xsl:text disable-output-escaping=\"yes\" >&gt;</xsl:text>\r\n	    	<!-- on affiche le service  -->\r\n			<h2>\r\n	           <a href=\"javascript:displayId(\'{$current}\');\"><img src=\"images/admin/skin/plus.gif\" alt=\"\"/></a>\r\n	           <a href=\"javascript:hideId(\'{$current}\');\"><img src=\"images/admin/skin/minus.gif\" alt=\"\"/></a>\r\n	          \r\n	           <xsl:value-of disable-output-escaping=\"yes\" select=\"actor-service\" />\r\n	        </h2>\r\n	        <!-- On ouvre la liste d\'acteur du service -->\r\n	        <xsl:text  disable-output-escaping=\"yes\" >&lt;</xsl:text>div style=\"visibility: hidden; display: none;\" id=\"<xsl:value-of select=\"$current\"/>\"<xsl:text  disable-output-escaping=\"yes\" >&gt;</xsl:text>\r\n	       		<xsl:text  disable-output-escaping=\"yes\" >&lt;</xsl:text>ul<xsl:text  disable-output-escaping=\"yes\" >&gt;</xsl:text>\r\n		       		<!-- on affiche le l\'acteur  -->\r\n		       		<xsl:call-template name=\"nameactor\"/>\r\n	       		\r\n			\r\n	  </xsl:when>\r\n	  <!-- Si le service a plusieur acteur et que l\'on est sur le dernier acteur -->\r\n	  <xsl:when test=\"string($precedent) = string($current) and string($suivant) != string($current)\">\r\n	  			\r\n	  			<!-- on affiche l\'acteur  -->\r\n	  			<xsl:call-template name=\"nameactor\"/>\r\n	  		<!-- On ferme la liste d\'acteur du service -->\r\n	  		<xsl:text  disable-output-escaping=\"yes\" >&lt;</xsl:text>/ul<xsl:text  disable-output-escaping=\"yes\" >&gt;</xsl:text>\r\n			&#160;\r\n			<xsl:text  disable-output-escaping=\"yes\" >&lt;</xsl:text>/div<xsl:text  disable-output-escaping=\"yes\" >&gt;</xsl:text>\r\n		<xsl:text  disable-output-escaping=\"yes\" >&lt;</xsl:text>/li<xsl:text  disable-output-escaping=\"yes\" >&gt;</xsl:text>\r\n	  </xsl:when>\r\n	  <!-- Si le service a plusieur acteur et que l\'on est sur un acteur en millieu de liste -->\r\n	  <xsl:when test=\"string($precedent) = string($current) and string($suivant) = string($current)\">\r\n				<!-- on affiche l\'acteur  -->\r\n	       		<xsl:call-template name=\"nameactor\"/>\r\n\r\n	  </xsl:when>\r\n	  <xsl:otherwise>\r\n	  \r\n	  </xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:if>\r\n</xsl:template>\r\n\r\n<!-- affiche le nom et le prénom de l\'acteur -->\r\n<xsl:template name=\"nameactor\">\r\n\r\n		<li style=\"list-style:none;\">\r\n       		<xsl:variable name=\"document-id\" select=\"actor-photo/file-resource/resource-document-id\"/>\r\n       		<img src=\"images/admin/skin/square.gif\" alt=\"\"/>\r\n			<a href=\"{$site-path}?document_id={$document-id}&#38;portlet_id={$portlet-id}\" target=\"_top\">      \r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"actor-firstname\" />\r\n				&#160;\r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"actor-lastname\" />\r\n			</a>\r\n			<br/>\r\n			&#160;&#160;<strong> Fonction :</strong><xsl:value-of disable-output-escaping=\"yes\" select=\"actor-function\"/><br/>\r\n			&#160;&#160;<strong> Téléphone : </strong><xsl:value-of disable-output-escaping=\"yes\" select=\"actor-telephone\"/><br/>\r\n		</li>\r\n\r\n</xsl:template>\r\n        \r\n</xsl:stylesheet> \r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (402,'Rubrique Liste de Documents - Sidebar','portlet_document_list_sidebar.xsl','<?xml version=\"1.0\"?>\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\n\n<xsl:param name=\"site-path\" select=\"site-path\" />\n<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\n    \n<xsl:template match=\"portlet\">\n\n	<xsl:variable name=\"device_class\">\n	<xsl:choose>\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\n		<xsl:otherwise></xsl:otherwise>\n	</xsl:choose>\n	</xsl:variable>\n\n	<div class=\"boxed2 {$device_class}\">\n		<h2>	\n			<xsl:if test=\"not(string(display-portlet-title)=\'1\')\">		\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />				\n			</xsl:if>\n			<xsl:if test=\"not(string(display-portlet-title)=\'0\')\">		\n				&#160;\n			</xsl:if>				\n		</h2>		\n        <xsl:apply-templates select=\"document-list-portlet\" />\n    </div>\n</xsl:template>\n\n\n<xsl:template match=\"document-list-portlet\">\n    <ul>\n        <xsl:apply-templates select=\"document\" />\n    </ul>\n</xsl:template>\n\n\n<xsl:template match=\"document\">\n    <li>\n        <a href=\"{$site-path}?document_id={document-id}&#38;portlet_id={$portlet-id}\" target=\"_top\">\n            <xsl:for-each select=\"descendant::*\">\n                <xsl:value-of select=\"document-title\" />\n           </xsl:for-each>\n	</a>           \n           <br />\n            <xsl:for-each select=\"descendant::*\">\n                <xsl:value-of select=\"document-summary\" />\n            </xsl:for-each>     \n            <br />\n			<br />				\n			<xsl:if test=\"(string(document-is-commentable)=\'1\')\">\n				<img src=\"images/local/skin/plugins/document/nb_comments.png\" alt=\"Nombre de commentaires\" title=\"Nombre de commentaires\" align=\"middle\"/>&#160;<xsl:value-of select=\"document-comment-nb\" />      \n			</xsl:if>   			\n    </li>\n</xsl:template>\n\n\n</xsl:stylesheet>\n\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (403,'Rubrique Document simple - Type Acteur','portlet_document_actor.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\"\r\n	xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n	<xsl:param name=\"site-path\" select=\"site-path\" />\r\n	<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n	<xsl:template match=\"portlet\">\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n	\r\n		<div class=\"portlet-background-colored  {$device_class} append-bottom\">\r\n			<xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n				<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n					<xsl:value-of disable-output-escaping=\"yes\"\r\n						select=\"portlet-name\" />\r\n				</h3>\r\n			</xsl:if>\r\n\r\n			<xsl:apply-templates select=\"document-portlet/document\" />\r\n			<xsl:if test=\"string(document-portlet/document)=\'\'\">\r\n				<xsl:text disable-output-escaping=\"yes\">\r\n					&amp;nbsp;\r\n				</xsl:text>\r\n			</xsl:if>\r\n\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"document\">\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\">\r\n			<xsl:output method=\"html\" indent=\"yes\" />\r\n			<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n				<xsl:apply-templates\r\n					select=\"document-xml-content/actor\" />\r\n			</xsl:if>\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"actor\">\r\n		<p>\r\n			<xsl:choose>\r\n				<xsl:when test=\"actor-photo/file-resource!=\'\'\">\r\n					<img\r\n						src=\"document?id={actor-photo/file-resource/resource-document-id}&amp;id_attribute={actor-photo/file-resource/resource-attribute-id}\"\r\n						align=\"left\" valign=\"middle\" hspace=\"20\" />\r\n				</xsl:when>\r\n				<xsl:otherwise></xsl:otherwise>\r\n			</xsl:choose>\r\n			<strong>\r\n				<xsl:value-of select=\"actor-firstname\" />\r\n				&#160;\r\n				<xsl:value-of select=\"actor-lastname\" />\r\n			</strong>\r\n		</p>\r\n		<p>\r\n			Fonction :\r\n			<xsl:value-of select=\"actor-function\" />\r\n		</p>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"file-resource\">\r\n		<xsl:choose>\r\n			<xsl:when\r\n				test=\"(resource-content-type=\'image/jpeg\' or resource-content-type=\'image/jpg\' or  resource-content-type=\'image/pjpeg\' or resource-content-type=\'image/gif\' or resource-content-type=\'image/png\')\">\r\n				<img\r\n					src=\"document?id={resource-document-id}&amp;id_attribute={resource-attribute-id}\"\r\n					align=\"right\" />\r\n			</xsl:when>\r\n			<xsl:otherwise>\r\n				<a\r\n					href=\"document?id={resource-document-id}&amp;id_attribute={resource-attribute-id}\">\r\n					<img\r\n						src=\"images/admin/skin/plugins/document/filetypes/file.png\"\r\n						border=\"0\" />\r\n				</a>\r\n			</xsl:otherwise>\r\n		</xsl:choose>\r\n	</xsl:template>\r\n</xsl:stylesheet>\r\n\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (504,'Rubrique Document simple - Type Brève','portlet_document_shortarticle.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\"\r\n	xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n	<xsl:param name=\"site-path\" select=\"site-path\" />\r\n	<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n	<xsl:template match=\"portlet\">\r\n	\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n		<div class=\"portlet-background-colored  {$device_class} append-bottom\">\r\n			<xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n				<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n					<xsl:value-of disable-output-escaping=\"yes\"\r\n						select=\"portlet-name\" />\r\n				</h3>\r\n			</xsl:if>\r\n\r\n			<xsl:apply-templates select=\"document-portlet/document\" />\r\n			<xsl:if test=\"string(document-portlet/document)=\'\'\">\r\n				<xsl:text disable-output-escaping=\"yes\">\r\n					&amp;nbsp;\r\n				</xsl:text>\r\n			</xsl:if>\r\n\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"document\">\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\">\r\n			<xsl:output method=\"html\" indent=\"yes\" />\r\n			<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n				<xsl:apply-templates\r\n					select=\"document-xml-content/shortarticle\" />\r\n			</xsl:if>\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"shortarticle\">\r\n		<p>\r\n			<strong>\r\n				<xsl:value-of select=\"document-title\" />\r\n			</strong>\r\n		</p>\r\n		<p>\r\n			<xsl:value-of disable-output-escaping=\"yes\"\r\n				select=\"document-summary\" />\r\n		</p>\r\n	</xsl:template>\r\n</xsl:stylesheet>\r\n\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (505,'Rubrique Document simple - Type Article','portlet_document_article.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\"\r\n	xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n	<xsl:param name=\"site-path\" select=\"site-path\" />\r\n	<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n	<xsl:template match=\"portlet\">\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n		<div class=\"portlet-background-colored  {$device_class} append-bottom\">\r\n			<xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n				<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n					<xsl:value-of disable-output-escaping=\"yes\"\r\n						select=\"portlet-name\" />\r\n				</h3>\r\n			</xsl:if>\r\n\r\n			<xsl:apply-templates select=\"document-portlet/document\" />\r\n			<xsl:if test=\"string(document-portlet/document)=\'\'\">\r\n				<xsl:text disable-output-escaping=\"yes\">&amp;nbsp;</xsl:text>\r\n			</xsl:if>\r\n\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"document\">\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\">\r\n			<xsl:output method=\"html\" indent=\"yes\" />\r\n			<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n				<xsl:apply-templates\r\n					select=\"document-xml-content/article\" />\r\n			</xsl:if>\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"article\">\r\n		<p>\r\n			<strong>\r\n				<xsl:value-of select=\"document-title\" />\r\n			</strong>\r\n		</p>\r\n		<p>\r\n			<xsl:choose>\r\n				<xsl:when test=\"article-attachment/file-resource!=\'\'\">\r\n					<xsl:apply-templates\r\n						select=\"article-attachment/file-resource\" />\r\n				</xsl:when>\r\n				<xsl:otherwise></xsl:otherwise>\r\n			</xsl:choose>\r\n		</p>\r\n		<p>\r\n			<xsl:value-of disable-output-escaping=\"yes\"\r\n				select=\"article-body\" />\r\n		</p>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"file-resource\">\r\n		<xsl:choose>\r\n			<xsl:when\r\n				test=\"(resource-content-type=\'image/jpeg\' or resource-content-type=\'image/jpg\' or  resource-content-type=\'image/pjpeg\' or resource-content-type=\'image/gif\' or resource-content-type=\'image/png\')\">\r\n				<img\r\n					src=\"document?id={resource-document-id}&amp;id_attribute={resource-attribute-id}\"\r\n					align=\"right\" />\r\n			</xsl:when>\r\n			<xsl:otherwise>\r\n				<a\r\n					href=\"document?id={resource-document-id}&amp;id_attribute={resource-attribute-id}\">\r\n					<img\r\n						src=\"images/admin/skin/plugins/document/filetypes/file.png\"\r\n						border=\"0\" />\r\n				</a>\r\n			</xsl:otherwise>\r\n		</xsl:choose>\r\n	</xsl:template>\r\n</xsl:stylesheet>\r\n\r\n');
