INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (508,'Rubrique Liste de Documents - Type Flash','portlet_document_flash_list.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:param name=\"site-path\" select=\"site-path\" />\r\n<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n<xsl:template match=\"portlet\">\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n    <div class=\"portlet-background-colored {$device_class} append-bottom\" >\r\n        <xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n            <h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n                <xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n            </h3>\r\n        </xsl:if>\r\n	<div class=\"portlet-background-content -lutece-border-radius-bottom\" >\r\n            <ul>\r\n                <xsl:apply-templates select=\"document-list-portlet/document\" />\r\n            </ul>\r\n	</div>\r\n    </div>\r\n</xsl:template>\r\n\r\n\r\n<xsl:template match=\"document\">      \r\n<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n    <li>\r\n        <a href=\"{$site-path}?document_id={document-id}&#38;portlet_id={$portlet-id}\" target=\"_top\"> \r\n            <xsl:for-each select=\"descendant::*\">   \r\n                <xsl:value-of select=\"document-title\" />\r\n            </xsl:for-each>    \r\n        </a>\r\n		<xsl:if test=\"(string(resource-is-votable)=\'1\')\">\r\n			<br />\r\n        	<xsl:variable name=\"resource-score\" select=\"resource-score\" />\r\n	        <img src=\"images/local/skin/plugins/rating/stars_{$resource-score}.png\" alt=\"Score\" title=\"Score\" />\r\n        </xsl:if>   \r\n        <xsl:if test=\"(string(is-download-stat)=\'1\')\">\r\n			<br />\r\n			#i18n{rating.resource_vote.labelDownloadCount} : <xsl:value-of select=\"resource-download-stat\" />\r\n		</xsl:if>\r\n        <br />\r\n        <xsl:for-each select=\"descendant::*\">           \r\n            <xsl:value-of select=\"document-summary\" />                 \r\n        </xsl:for-each>  \r\n        <br />            \r\n        <xsl:if test=\"(string(document-is-commentable)=\'1\')\">\r\n            Nombre de commentaires : <xsl:value-of select=\"document-comment-nb\" />      \r\n        </xsl:if>   \r\n    </li>\r\n</xsl:if>\r\n</xsl:template>\r\n\r\n</xsl:stylesheet>\r\n\r\n');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (509,'Rubrique Document simple - Type Flash','portlet_document_flash.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\"\r\n	xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n	<xsl:param name=\"site-path\" select=\"site-path\" />\r\n	<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n	<xsl:template match=\"portlet\">\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n		<div class=\"portlet-background-colored {$device_class} append-bottom\">\r\n			<xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n				<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n					<xsl:value-of disable-output-escaping=\"yes\"\r\n						select=\"portlet-name\" />\r\n				</h3>\r\n			</xsl:if>\r\n\r\n			<xsl:apply-templates select=\"document-portlet/document\" />\r\n			<xsl:if test=\"string(document-portlet/document)=\'\'\">\r\n				<xsl:text disable-output-escaping=\"yes\">\r\n					&amp;nbsp;\r\n				</xsl:text>\r\n			</xsl:if>\r\n\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"document\">\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\">\r\n			<xsl:output method=\"html\" indent=\"yes\" />\r\n			<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n				<xsl:apply-templates\r\n					select=\"document-xml-content/flash\" />\r\n			</xsl:if>\r\n		</div>\r\n	</xsl:template>\r\n\r\n	<xsl:template match=\"flash\">\r\n		<h1><xsl:value-of select=\"document-title\" /></h1>\r\n		<p>\r\n			<xsl:value-of select=\"document-summary\" />\r\n		</p>\r\n		<p>\r\n			<xsl:choose>\r\n				<xsl:when test=\"(flash-object/file-resource/resource-content-type=\'application/x-shockwave-flash\')\" >\r\n					<object \r\n						data=\"document?id={flash-object/file-resource/resource-document-id}&amp;id_attribute={flash-object/file-resource/resource-attribute-id}\" \r\n						type=\"application/x-shockwave-flash\" \r\n						width=\"{flash-width}\" \r\n						height=\"{flash-height}\"\r\n						align=\"{flash-align}\"\r\n						classid=\"clsid:D27CDB6E-AE6D-11cf-96B8-444553540000\"\r\n						codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=6,0,0,0\"\r\n					>\r\n						<param name=\"Autostart\" value=\"{flash-autostart}\" />\r\n						<param name=\"Quality\" value=\"{flash-quality}\" />\r\n						<param name=\"Loop\" value=\"{flash-loop}\" />\r\n						<param name=\"menu\" value=\"{flash-menu}\" />\r\n						<param name=\"allowScriptAccess\" value=\"samedomain\" />\r\n						<param name=\"movie\" value=\"document?id={flash-object/file-resource/resource-document-id}&amp;id_attribute={flash-object/file-resource/resource-attribute-id}\" />\r\n						<EMBED \r\n							src=\"document?id={flash-object/file-resource/resource-document-id}&amp;id_attribute={flash-object/file-resource/resource-attribute-id}\" \r\n							type=\"application/x-shockwave-flash\"\r\n							width=\"{flash-width}\" \r\n							height=\"{flash-height}\"\r\n							align=\"{flash-align}\"\r\n							quality=\"{flash-quality}\"\r\n							loop=\"{flash-loop}\"\r\n							menu=\"{flash-menu}\"\r\n							allowScriptAccess=\"samedomain\"\r\n							pluginspace=\"http://www.macromedia.com/shockwave/download/index.cgi?P1_Prod_Version=ShockwaveFlash\">\r\n						</EMBED>\r\n					</object>\r\n				</xsl:when>\r\n			</xsl:choose>\r\n		</p>\r\n		<blockquote>\r\n			<xsl:value-of disable-output-escaping=\"yes\" select=\"flash-credits\" />\r\n		</blockquote>\r\n	</xsl:template>\r\n	\r\n</xsl:stylesheet>');
INSERT INTO core_stylesheet (id_stylesheet, description, file_name, source) VALUES (510,'Rubrique Liste de Documents - Défaut Download','portlet_document_list_download.xsl','<?xml version=\"1.0\"?>\r\n<xsl:stylesheet version=\"1.0\" xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\">\r\n\r\n<xsl:param name=\"site-path\" select=\"site-path\" />\r\n<xsl:variable name=\"portlet-id\" select=\"portlet/portlet-id\" />\r\n\r\n<xsl:template match=\"portlet\">\r\n	<xsl:variable name=\"device_class\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"string(display-on-small-device)=\'0\'\">hide-for-small</xsl:when>\r\n		<xsl:otherwise></xsl:otherwise>\r\n	</xsl:choose>\r\n	</xsl:variable>\r\n\r\n	<div class=\"portlet-background-colored  {$device_class} append-bottom\" >\r\n        <xsl:if test=\"not(string(display-portlet-title)=\'1\')\">\r\n			<h3 class=\"portlet-background-colored-header -lutece-border-radius-top\">\r\n				<xsl:value-of disable-output-escaping=\"yes\" select=\"portlet-name\" />\r\n			</h3>\r\n        </xsl:if>\r\n		<div class=\"portlet-background-content -lutece-border-radius-bottom\" >\r\n		     <ul>\r\n         	       <xsl:apply-templates select=\"document-list-portlet/document\" />\r\n             </ul>  \r\n		</div>\r\n	</div>\r\n</xsl:template>\r\n\r\n\r\n<xsl:template match=\"document\">      \r\n<xsl:if test=\"not(string(document-xml-content)=\'null\')\">\r\n    <li>\r\n        <a href=\"document?id={document-id}&amp;id_attribute=48\" target=\"_blank\">      \r\n        	<xsl:for-each select=\"descendant::*\">\r\n                <xsl:apply-templates select=\"file-resource\" />\r\n           </xsl:for-each>  \r\n          	<xsl:for-each select=\"descendant::*\">\r\n                <xsl:value-of select=\"document-title\" />\r\n           </xsl:for-each>  \r\n        </a>\r\n		<xsl:if test=\"(string(resource-is-votable)=\'1\')\">\r\n			<br />\r\n        	<xsl:variable name=\"resource-score\" select=\"resource-score\" />\r\n	        <img src=\"images/local/skin/plugins/rating/stars_{$resource-score}.png\" alt=\"Score\" title=\"Score\" />\r\n        </xsl:if>    \r\n        <xsl:if test=\"(string(is-download-stat)=\'1\')\">\r\n			<br />\r\n			#i18n{rating.resource_vote.labelDownloadCount} : <xsl:value-of select=\"resource-download-stat\" />\r\n		</xsl:if>\r\n		<br />\r\n	    <xsl:for-each select=\"descendant::*\">\r\n        	<xsl:value-of select=\"document-summary\" />\r\n        </xsl:for-each>\r\n    </li>\r\n</xsl:if>\r\n</xsl:template>\r\n\r\n<xsl:template match=\"file-resource\">\r\n	<xsl:choose>\r\n		<xsl:when test=\"(resource-content-type=\'image/jpeg\' or resource-content-type=\'image/jpg\' or resource-content-type=\'image/pjpeg\' or resource-content-type=\'image/gif\' or resource-content-type=\'image/png\')\" >\r\n			<img src=\"images/local/skin/plugins/document/filetypes/image.png\" border=\"0\" alt=\"\" width=\"32\" height=\"32\"/>\r\n		</xsl:when>\r\n		<xsl:when test=\"(resource-content-type=\'application/pdf\')\" >\r\n			<img src=\"images/local/skin/plugins/document/filetypes/pdf.png\" border=\"0\" alt=\"\" width=\"32\" height=\"32\" />\r\n		</xsl:when>\r\n		<xsl:when test=\"(resource-content-type=\'application/msword\' or resource-content-type=\'application/vnd.oasis.opendocument.text\')\" >\r\n			<img src=\"images/local/skin/plugins/document/filetypes/text.png\" border=\"0\" alt=\"\" width=\"32\" height=\"32\" />\r\n		</xsl:when>\r\n		<xsl:when test=\"(resource-content-type=\'application/excel\' or resource-content-type=\'application/vnd.ms-excel\' or resource-content-type=\'application/vnd.oasis.opendocument.spreadsheet\')\" >\r\n			<img src=\"images/local/skin/plugins/document/filetypes/spreadsheet.png\" border=\"0\" alt=\"\" width=\"32\" height=\"32\" />\r\n		</xsl:when>\r\n		<xsl:otherwise>\r\n		   <img src=\"images/local/skin/plugins/document/filetypes/file.png\" border=\"0\" alt=\"\" width=\"32\" height=\"32\"/>\r\n		</xsl:otherwise>        \r\n	</xsl:choose>\r\n</xsl:template>              \r\n\r\n\r\n</xsl:stylesheet>\r\n\r\n');
